CREATE TABLE lbcat.table_datetime (id INT AUTO_INCREMENT NOT NULL, p0 datetime(0) DEFAULT NOW() NOT NULL, p1 datetime(1) DEFAULT NOW(1) NOT NULL, p2 datetime(2) DEFAULT NOW(2) NOT NULL, p3 datetime(3) DEFAULT NOW(3) NOT NULL, p4 datetime(4) DEFAULT NOW(4) NOT NULL, p5 datetime(5) DEFAULT NOW(5) NOT NULL, p6 datetime(6) DEFAULT NOW(6) NOT NULL, CONSTRAINT PK_TABLE_DATETIME PRIMARY KEY (id))

CREATE TABLE lbcat.table_timestamp (id INT AUTO_INCREMENT NOT NULL, p0 TIMESTAMP(0) DEFAULT NOW() NOT NULL, p1 TIMESTAMP(1) DEFAULT NOW(1) NOT NULL, p2 TIMESTAMP(2) DEFAULT NOW(2) NOT NULL, p3 TIMESTAMP(3) DEFAULT NOW(3) NOT NULL, p4 TIMESTAMP(4) DEFAULT NOW(4) NOT NULL, p5 TIMESTAMP(5) DEFAULT NOW(5) NOT NULL, p6 TIMESTAMP(6) DEFAULT NOW(6) NOT NULL, CONSTRAINT PK_TABLE_TIMESTAMP PRIMARY KEY (id))