CREATE TABLE ${CATALOG_NAME}.PUBLIC.authors_data AS SELECT DISTINCT email AS authors_email FROM ${CATALOG_NAME}.PUBLIC.authors WHERE email IS NOT NULL
ALTER TABLE ${CATALOG_NAME}.PUBLIC.authors_data ALTER COLUMN  authors_email SET NOT NULL
ALTER TABLE ${CATALOG_NAME}.PUBLIC.authors_data ADD PRIMARY KEY (authors_email)
ALTER TABLE ${CATALOG_NAME}.PUBLIC.authors ADD CONSTRAINT FK_AUTHORS_AUTHORS_DATA FOREIGN KEY (email) REFERENCES ${CATALOG_NAME}.PUBLIC.authors_data (authors_email)